import './Nav.css'
import React, { useState, useRef } from "react";
import { Link } from "react-router-dom"
import { Route } from 'react-router-dom'
import Title from "../Title/Title"

function Nav() {
    const [show, setShow] = useState(false)
    const titleRef = useRef()


    function handleOnClick() {
        window.scrollTo({
            top: 1530,
            behavior: 'smooth'
        })
    }


    function links() {
        return (
            <div className="links-container">

                <button onClick={() => window.scrollTo({ top: 1530,behavior: 'smooth'})}> <h5> About</h5> </button>
                <a ><h5>Projects</h5></a>
                <Link to="/resume"><h5>Resume</h5></Link>
                <Link to="/contact"><h5>Contact</h5></Link>
            </div >
        )
    }

    return (
        <div className="nav-container">
            <div id="name">
                <Link to="/" id="nDec"><h1>Aslan Shaken</h1></Link>
            </div>
            <div className="hamburger-container">
                <div id="hamburger-menu">
                    <img
                        onClick={() => setShow(!show)}
                        id="hamburger-img"
                        src={show
                            ? "https://icons-for-free.com/iconfiles/png/512/x-1321215629555778185.png"
                            : "https://img.icons8.com/carbon-copy/2x/menu.png"}
                    />
                    {show && links()}
                </div>
            </div>
        </div>
    )
}

export default Nav
